mapper.select:
SELECT COUNT(*)
    FROM SHOP_PHOTO
    WHERE SID=#{sid}

mapper.insert:
INSERT INTO SHOP_PHOTO(SID
,PHOTO1, SPHOTO1
,PHOTO2, SPHOTO2
,PHOTO3, SPHOTO3
,PHOTO4, SPHOTO4
,PHOTO5, SPHOTO5
)
VALUES(#{sid}
,#{shopPhotoDto.photo1}, #{shopPhotoDto.sphoto1}
,#{shopPhotoDto.photo2}, #{shopPhotoDto.sphoto2}
,#{shopPhotoDto.photo3}, #{shopPhotoDto.sphoto3}
,#{shopPhotoDto.photo4}, #{shopPhotoDto.sphoto4}
,#{shopPhotoDto.photo5}, #{shopPhotoDto.sphoto5}
)

mapper.update:
UPDATE SHOP_PHOTO SET
SID=#{shopPhotoDto.sid}
,PHOTO1=#{shopPhotoDto.photo1, jdbcType=VARCHAR}, SPHOTO1=#{shopPhotoDto.sphoto1, jdbcType=VARCHAR}
,PHOTO2=#{shopPhotoDto.photo2, jdbcType=VARCHAR}, SPHOTO2=#{shopPhotoDto.sphoto2, jdbcType=VARCHAR}
,PHOTO3=#{shopPhotoDto.photo3, jdbcType=VARCHAR}, SPHOTO3=#{shopPhotoDto.sphoto3, jdbcType=VARCHAR}
,PHOTO4=#{shopPhotoDto.photo4, jdbcType=VARCHAR}, SPHOTO4=#{shopPhotoDto.sphoto4, jdbcType=VARCHAR}
,PHOTO5=#{shopPhotoDto.photo5, jdbcType=VARCHAR}, SPHOTO5=#{shopPhotoDto.sphoto5, jdbcType=VARCHAR}
WHERE SID=#{shopPhotoDto.sid}
